export default `body,h1,h2,h3,p,ul,li,button{margin:0;padding:0;box-sizing:border-box;font-family:Arial,sans-serif}body{display:flex;flex-direction:column;min-height:100vh;transition:background-color .3s ease,color .3s ease;font-size:16px;color:var(--text-color);background-color:var(--bg-color);line-height:1.5}.container{flex:1;max-width:100%;margin:0 auto;padding:1rem;background-color:var(--card-bg-color);border-radius:8px;box-shadow:0 2px 4px #0000001a}header{text-align:center;margin-bottom:1rem;padding:1rem 0;font-size:24px}body.dark header{background-color:#6200ea}button,input{padding:.5rem 1rem;border:none;border-radius:4px;cursor:pointer}button.add-task{background-color:var(--button-bg-color);color:var(--button-text-color);transition:background-color .3s ease}button.add-task:hover{background-color:#0056b3}.task{display:flex;justify-content:space-between;align-items:center;padding:.5rem 1rem;margin:.5rem 0;border-radius:4px;background-color:#f0f0f0}.task .completed{text-decoration:line-through;color:gray}button.remove-task{background-color:#dc3545;color:#fff}button.remove-task:hover{background-color:#a71d2a}body.light{--bg-color: #f9f9f9;--text-color: #333;--card-bg-color: #fff;--button-bg-color: #007bff;--button-text-color: #fff}body.dark{--bg-color: #121212;--text-color: #e0e0e0;--card-bg-color: #1f1f1f;--button-bg-color: #bb86fc;--button-text-color: #000}.theme-toggle{display:flex;justify-content:center;margin:1rem 0}.toggle-button{padding:.5rem 1.5rem;font-size:1rem;font-weight:700;border:none;border-radius:8px;cursor:pointer;transition:background-color .3s ease,color .3s ease}body.light .toggle-button{background-color:#007bff;color:#fff}body.dark .toggle-button{background-color:#bb86fc;color:#000}.toggle-button:hover{opacity:.9}@media (min-width: 600px){.container{max-width:600px}}header,.theme-toggle,.container{padding:0 1rem;text-align:center}.todo-input{display:flex;gap:10px;margin-bottom:20px}.todo-input input{flex:1;padding:8px;border-radius:5px;border:1px solid #ccc;font-size:1rem;transition:background-color .3s ease,color .3s ease}.todo-input button{padding:8px 16px;background-color:var(--button-bg-color);color:var(--button-text-color);border:none;cursor:pointer;border-radius:5px;transition:background-color .3s ease}.todo-input button:hover{background-color:#0056b3}body.dark .todo-input input{background-color:#444;color:#e0e0e0;border:1px solid #666}body.dark .todo-input button{background-color:#6200ea}body.dark .todo-input button:hover{background-color:#3700b3}.todo-item{display:flex;align-items:center;padding:10px;margin-bottom:10px;border-radius:8px;transition:background-color .3s ease,color .3s ease}.todo-item span{margin-left:10px}body.light .todo-item{background-color:#fff;color:#333}body.dark .todo-item{background-color:#333;color:#e0e0e0}body.dark .todo-item span{color:#e0e0e0}.task-list{display:flex;flex-direction:column}.task-list .task{background-color:#f0f0f0;margin-bottom:10px}body.dark .task-list .task{background-color:#1f1f1f}body.light{background-color:#f9f9f9;color:#333}body.dark{background-color:#121212;color:#e0e0e0}footer{position:fixed;bottom:0;left:0;width:100%;text-align:center;padding:1rem;background-color:var(--footer-bg-color);color:var(--footer-text-color);border-top:1px solid var(--footer-border-color);font-size:14px}body.light footer{--footer-bg-color: #f1f1f1;--footer-text-color: #333;--footer-border-color: #ddd}body.dark footer{--footer-bg-color: #222;--footer-text-color: #e0e0e0;--footer-border-color: #444}
`;